mapper.select:
select o.receiptcode, o.warehousecode, w.warehousetext, o.materialcode, m.materialtext, o.operationtype, o.quantity, o.createuser, o.createdatetime, o.modifyuser, o.modifydatetime from w_operationstock o left join w_material m on o.materialcode = m.materialcode left join w_warehouse w on o.warehousecode = w.warehousecode
and o.receiptcode like concat(concat('%', #{receiptCode, jdbcType = VARCHAR}), '%')
and o.warehousecode like concat(concat('%', #{warehouseCode, jdbcType = VARCHAR}), '%')
and o.materialcode like concat(concat('%', #{materialCode, jdbcType = VARCHAR}), '%')
and o.operationtype = #{operationType, jdbcType = VARCHAR}
order by ${orderField} ${orderDirection}
