mapper.select:
select o.receiptcode, o.warehousecode, w.warehousetext, o.materialcode, m.materialtext, o.operationtype, o.quantity, o.price, o.stovecode, o.batchcode, o.weight, o.kgnum, o.productcode, o.operator, o.createuser, o.createdatetime, o.modifyuser, o.modifydatetime from w_operationstock o left join w_material m on o.materialcode = m.materialcode left join w_warehouse w on o.warehousecode = w.warehousecode
and o.receiptcode like concat(concat('%', #{receiptCode, jdbcType = VARCHAR}), '%')
and o.warehousecode = #{warehouseCode, jdbcType = VARCHAR}
and o.materialcode = #{materialCode, jdbcType = VARCHAR}
and o.operationtype = #{operationType, jdbcType = VARCHAR}
and date_format(o.createdatetime, '%Y-%m-%d') >= #{createDateStart, jdbcType = VARCHAR}
and date_format(o.createdatetime, '%Y-%m-%d') <= #{createDateEnd, jdbcType = VARCHAR}
and o.stovecode like concat(concat('%', #{stoveCode, jdbcType = VARCHAR}), '%')
and o.batchcode like concat(concat('%', #{batchCode, jdbcType = VARCHAR}), '%')
and o.productcode like concat(concat('%', #{productCode, jdbcType = VARCHAR}), '%')
and o.operator like concat(concat('%', #{operator, jdbcType = VARCHAR}), '%')
order by ${orderField} ${orderDirection}
